import { getUserIndex, changeStatus, add, edit, del } from '@/api/user';
import { getGroups } from '@/api/auth';

var editButton = function editButton(vm, h, currentRow, index) {
  if (vm.buttonShow.edit) {
    return h('Button', {
      props: {
        type: 'primary'
      },
      style: {
        margin: '0 5px'
      },
      on: {
        'click': function click() {
          vm.formItem.id = currentRow.id;
          vm.formItem.username = currentRow.username;
          vm.formItem.nickname = currentRow.nickname;
          vm.formItem.password = 'ApiAdmin';
          getGroups().then(function (response) {
            vm.groupList = response.data.data.list;
          });
          vm.formItem.group_id = currentRow.group_id;
          vm.modalSetting.show = true;
          vm.modalSetting.index = index;
        }
      }
    }, vm.$t('edit_button'));
  }
};

var deleteButton = function deleteButton(vm, h, currentRow, index) {
  if (vm.buttonShow.del) {
    return h('Poptip', {
      props: {
        confirm: true,
        title: '您确定要删除这条数据吗? ',
        transfer: true
      },
      on: {
        'on-ok': function onOk() {
          del(currentRow.id).then(function (response) {
            vm.tableData.splice(index, 1);
            vm.$Message.success(response.data.msg);
          });
          currentRow.loading = false;
        }
      }
    }, [h('Button', {
      style: {
        margin: '0 5px'
      },
      props: {
        type: 'error',
        placement: 'top',
        loading: currentRow.isDeleting
      }
    }, vm.$t('delete_button'))]);
  }
};

export default {
  name: 'system_user',
  data: function data() {
    var _this = this;

    return {
      columnsList: [{
        title: '序号',
        type: 'index',
        width: 65,
        align: 'center'
      }, {
        title: '用户账号',
        align: 'center',
        key: 'username',
        minWidth: 120
      }, {
        title: '用户昵称',
        align: 'center',
        key: 'nickname',
        width: 160
      }, {
        title: '登录次数',
        align: 'center',
        render: function render(h, params) {
          return h('span', params.row.userData === null ? '' : params.row.userData.login_times);
        },
        width: 100
      }, {
        title: '最后登录时间',
        align: 'center',
        render: function render(h, params) {
          return h('span', params.row.userData === null ? '' : params.row.userData.last_login_time);
        },
        width: 170
      }, {
        title: '最后登录IP',
        align: 'center',
        render: function render(h, params) {
          return h('span', params.row.userData === null ? '' : params.row.userData.last_login_ip);
        },
        width: 160
      }, {
        title: '状态',
        align: 'center',
        width: 100,
        render: function render(h, params) {
          var vm = _this;
          return h('i-switch', {
            attrs: {
              size: 'large'
            },
            props: {
              'true-value': 1,
              'false-value': 0,
              value: params.row.status,
              disabled: !vm.buttonShow.changeStatus
            },
            on: {
              'on-change': function onChange(status) {
                changeStatus(status, params.row.id).then(function (response) {
                  vm.$Message.success(response.data.msg);
                  vm.getList();
                });
              }
            }
          }, [h('span', {
            slot: 'open'
          }, vm.$t('open_choose')), h('span', {
            slot: 'close'
          }, vm.$t('close_choose'))]);
        }
      }, {
        title: '操作',
        align: 'center',
        width: 200,
        render: function render(h, params) {
          return h('div', [editButton(_this, h, params.row, params.index), deleteButton(_this, h, params.row, params.index)]);
        }
      }],
      tableData: [],
      groupList: [],
      tableShow: {
        currentPage: 1,
        pageSize: 10,
        listCount: 0
      },
      searchConf: {
        type: '',
        keywords: '',
        status: ''
      },
      modalSetting: {
        show: false,
        loading: false,
        index: 0
      },
      formItem: {
        username: '',
        nickname: '',
        password: '',
        group_id: [],
        id: 0
      },
      ruleValidate: {
        username: [{
          required: true,
          message: '用户账号不能为空',
          trigger: 'blur'
        }],
        nickname: [{
          required: true,
          message: '用户昵称不能为空',
          trigger: 'blur'
        }],
        password: [{
          required: true,
          message: '用户密码不能为空',
          trigger: 'blur'
        }]
      },
      buttonShow: {
        edit: true,
        del: true,
        changeStatus: true
      },
      listLoading: false
    };
  },
  created: function created() {
    var vm = this;
    vm.getList();
    vm.hasRule('User/edit').then(function (res) {
      vm.buttonShow.edit = res;
    });
    vm.hasRule('User/del').then(function (res) {
      vm.buttonShow.del = res;
    });
    vm.hasRule('User/changeStatus').then(function (res) {
      vm.buttonShow.changeStatus = res;
    });
  },
  methods: {
    alertAdd: function alertAdd() {
      var vm = this;
      getGroups().then(function (response) {
        vm.groupList = response.data.data.list;
      });
      this.modalSetting.show = true;
    },
    submit: function submit() {
      var vm = this;
      this.$refs['myForm'].validate(function (valid) {
        if (valid) {
          vm.modalSetting.loading = true;

          if (vm.formItem.id === 0) {
            add(vm.formItem).then(function (response) {
              vm.$Message.success(response.data.msg);
              vm.getList();
              vm.cancel();
            }).catch(function () {
              vm.modalSetting.loading = false;
            });
          } else {
            edit(vm.formItem).then(function (response) {
              vm.$Message.success(response.data.msg);
              vm.getList();
              vm.cancel();
            }).catch(function () {
              vm.modalSetting.loading = false;
            });
          }
        }
      });
    },
    cancel: function cancel() {
      this.modalSetting.show = false;
    },
    doCancel: function doCancel(data) {
      if (!data) {
        this.formItem.id = 0;
        this.$refs['myForm'].resetFields();
        this.modalSetting.loading = false;
        this.modalSetting.index = 0;
      }
    },
    changePage: function changePage(page) {
      this.tableShow.currentPage = page;
      this.getList();
    },
    changeSize: function changeSize(size) {
      this.tableShow.pageSize = size;
      this.getList();
    },
    search: function search() {
      this.tableShow.currentPage = 1;
      this.getList();
    },
    getList: function getList() {
      var vm = this;
      var params = {
        page: vm.tableShow.currentPage,
        size: vm.tableShow.pageSize,
        type: vm.searchConf.type,
        keywords: vm.searchConf.keywords,
        status: vm.searchConf.status
      };
      vm.listLoading = true;
      getUserIndex(params).then(function (response) {
        vm.tableData = response.data.data.list;
        vm.tableShow.listCount = response.data.data.count;
        vm.listLoading = false;
      });
    }
  }
};
