import { getList as _getList, changeStatus, add, edit, del } from '@/api/app-group';
import { getHash } from '@/api/interface';

var editButton = function editButton(vm, h, currentRow, index) {
  if (vm.buttonShow.edit) {
    return h('Button', {
      props: {
        type: 'primary'
      },
      style: {
        margin: '0 5px'
      },
      on: {
        'click': function click() {
          vm.formItem.id = currentRow.id;
          vm.formItem.name = currentRow.name;
          vm.formItem.hash = currentRow.hash;
          vm.formItem.description = currentRow.description;
          vm.modalSetting.show = true;
          vm.modalSetting.index = index;
        }
      }
    }, vm.$t('edit_button'));
  }
};

var deleteButton = function deleteButton(vm, h, currentRow, index) {
  if (vm.buttonShow.del) {
    return h('Poptip', {
      props: {
        confirm: true,
        title: '您确定要删除这条数据吗? ',
        transfer: true
      },
      on: {
        'on-ok': function onOk() {
          del(currentRow.hash).then(function (response) {
            vm.tableData.splice(index, 1);
            vm.$Message.success(response.data.msg);
          });
        }
      }
    }, [h('Button', {
      style: {
        margin: '0 5px'
      },
      props: {
        type: 'error',
        placement: 'top',
        loading: currentRow.isDeleting
      }
    }, vm.$t('delete_button'))]);
  }
};

export default {
  name: 'interface_group',
  data: function data() {
    var _this = this;

    return {
      columnsList: [{
        title: '序号',
        type: 'index',
        width: 65,
        align: 'center'
      }, {
        title: '应用组名称',
        align: 'center',
        key: 'name'
      }, {
        title: '应用组描述',
        align: 'center',
        key: 'description'
      }, {
        title: '应用组标识',
        align: 'center',
        key: 'hash',
        width: 140
      }, {
        title: '应用组状态',
        align: 'center',
        width: 120,
        render: function render(h, params) {
          var vm = _this;
          return h('i-switch', {
            attrs: {
              size: 'large'
            },
            props: {
              'true-value': 1,
              'false-value': 0,
              value: params.row.status,
              disabled: !vm.buttonShow.changeStatus
            },
            on: {
              'on-change': function onChange(status) {
                changeStatus(status, params.row.id).then(function (response) {
                  vm.$Message.success(response.data.msg);
                  vm.getList();
                });
              }
            }
          }, [h('span', {
            slot: 'open'
          }, vm.$t('open_choose')), h('span', {
            slot: 'close'
          }, vm.$t('close_choose'))]);
        }
      }, {
        title: '操作',
        align: 'center',
        width: 200,
        render: function render(h, params) {
          return h('div', [editButton(_this, h, params.row, params.index), deleteButton(_this, h, params.row, params.index)]);
        }
      }],
      tableData: [],
      tableShow: {
        currentPage: 1,
        pageSize: 10,
        listCount: 0
      },
      searchConf: {
        type: '',
        keywords: '',
        status: ''
      },
      modalSetting: {
        show: false,
        loading: false,
        index: 0
      },
      formItem: {
        description: '',
        name: '',
        hash: '',
        id: 0
      },
      ruleValidate: {
        name: [{
          required: true,
          message: '应用组名称不能为空',
          trigger: 'blur'
        }]
      },
      buttonShow: {
        edit: true,
        del: true,
        changeStatus: true
      },
      listLoading: false
    };
  },
  created: function created() {
    var vm = this;
    vm.getList();
    vm.hasRule('AppGroup/edit').then(function (res) {
      vm.buttonShow.edit = res;
    });
    vm.hasRule('AppGroup/del').then(function (res) {
      vm.buttonShow.del = res;
    });
    vm.hasRule('AppGroup/changeStatus').then(function (res) {
      vm.buttonShow.changeStatus = res;
    });
  },
  methods: {
    alertAdd: function alertAdd() {
      var vm = this;
      getHash().then(function (response) {
        vm.formItem.hash = response.data.data.hash;
      });
      vm.modalSetting.show = true;
    },
    submit: function submit() {
      var vm = this;
      vm.$refs['myForm'].validate(function (valid) {
        if (valid) {
          vm.modalSetting.loading = true;

          if (vm.formItem.id === 0) {
            add(vm.formItem).then(function (response) {
              vm.$Message.success(response.data.msg);
              vm.getList();
              vm.cancel();
            }).catch(function () {
              vm.modalSetting.loading = false;
            });
          } else {
            edit(vm.formItem).then(function (response) {
              vm.$Message.success(response.data.msg);
              vm.getList();
              vm.cancel();
            }).catch(function () {
              vm.modalSetting.loading = false;
            });
          }
        }
      });
    },
    cancel: function cancel() {
      this.modalSetting.show = false;
    },
    changePage: function changePage(page) {
      this.tableShow.currentPage = page;
      this.getList();
    },
    changeSize: function changeSize(size) {
      this.tableShow.pageSize = size;
      this.getList();
    },
    search: function search() {
      this.tableShow.currentPage = 1;
      this.getList();
    },
    getList: function getList() {
      var vm = this;
      vm.listLoading = true;

      _getList({
        page: vm.tableShow.currentPage,
        size: vm.tableShow.pageSize,
        type: vm.searchConf.type,
        keywords: vm.searchConf.keywords,
        status: vm.searchConf.status
      }).then(function (response) {
        vm.tableData = response.data.data.list;
        vm.tableShow.listCount = response.data.data.count;
        vm.listLoading = false;
      });
    },
    doCancel: function doCancel(data) {
      if (!data) {
        this.formItem.id = 0;
        this.$refs['myForm'].resetFields();
        this.modalSetting.loading = false;
        this.modalSetting.index = 0;
      }
    }
  }
};
