import IconChoose from '_c/icon-choose';
import { getList as _getList, add, edit, del } from '@/api/menu';
export default {
  name: 'system_menu',
  components: {
    IconChoose: IconChoose
  },
  data: function data() {
    return {
      loading: false,
      // 树加载状态
      maxHeight: '',
      modalVisible: false,
      // 添加显示
      selectList: [],
      // 多选数据
      showParent: false,
      // 显示上级标识
      modalTitle: '',
      // 添加标题
      editTitle: '',
      // 编辑节点名称
      searchKey: '',
      // 搜索树
      form: {
        id: '',
        level: '',
        icon: '',
        router: '',
        method: 1,
        url: '',
        component: '',
        auth: 0,
        log: 0,
        permission: 0,
        show: 1,
        sort: 0
      },
      formAdd: {
        title: '',
        level: '',
        icon: '',
        router: '',
        method: 1,
        url: '',
        component: '',
        auth: 1,
        log: 1,
        permission: 1,
        show: 1,
        sort: 0
      },
      formValidate: {
        title: [{
          required: true,
          message: '名称不能为空',
          trigger: 'blur'
        }],
        sort: [{
          required: true,
          type: 'number',
          message: '排序值不能为空',
          trigger: 'blur'
        }]
      },
      submitLoading: false,
      data: []
    };
  },
  methods: {
    handleDropdown: function handleDropdown(name) {
      if (name === 'expandOne') {
        this.getList(1);
      } else if (name === 'expandTwo') {
        this.getList(2);
      } else if (name === 'expandAll') {
        this.getList(3);
      } else if (name === 'refresh') {
        this.getList();
      }
    },
    getList: function getList(level) {
      var vm = this;
      level = level || 0;
      var data = sessionStorage.getItem('menuList');

      if (data === null || level === 0) {
        vm.loading = true;

        _getList().then(function (response) {
          sessionStorage.setItem('menuList', JSON.stringify(response.data.data.list));
          vm.data = vm.reBuildMenu(response.data.data.list, level);
          vm.loading = false;
        });
      } else {
        vm.data = vm.reBuildMenu(JSON.parse(data), level);
      }

      vm.selectList = [];
    },
    reBuildMenu: function reBuildMenu(data, level) {
      var vm = this;
      data.forEach(function (e) {
        if (e.children && e.children.length > 0) {
          if (e.level < level) {
            e.expand = true;
          }

          e.children = vm.reBuildMenu(e.children, level);
        }
      });
      return data;
    },
    selectTree: function selectTree(v) {
      if (v[0] && v[0].id !== this.form.id) {
        this.form = JSON.parse(JSON.stringify(v[0]));
        this.form.level = this.form.level.toString();
        this.form.url = this.form.url.slice(6);
        this.editTitle = this.form.title;
      } else {
        this.cancelEdit();
      }
    },
    cancelEdit: function cancelEdit() {
      var data = this.$refs.tree.getSelectedNodes()[0];

      if (data) {
        data.selected = false;
      }

      this.handleReset();
      this.form.id = '';
      this.editTitle = '';
    },
    handleReset: function handleReset() {
      this.$refs.form.resetFields();
      this.form.level = '';
      this.form.auth = 0;
      this.form.log = 0;
      this.form.permission = 0;
      this.form.show = 1;
    },
    add: function add() {
      if (!this.form.id) {
        this.$Message.warning('请先点击选择一个菜单');
        return;
      }

      this.modalTitle = '添加子菜单';
      this.showParent = true;
      this.formAdd = {
        fid: this.form.id,
        title: '',
        level: (parseInt(this.form.level) + 1).toString(),
        icon: '',
        router: '',
        url: '',
        method: 1,
        component: '',
        auth: 1,
        log: 1,
        permission: 1,
        show: 1,
        sort: 0
      };
      this.modalVisible = true;
    },
    addRoot: function addRoot() {
      this.modalTitle = '添加顶级菜单';
      this.showParent = false;
      this.formAdd = {
        fid: 0,
        title: '',
        level: '1',
        icon: '',
        router: '',
        url: '',
        method: 1,
        component: '',
        auth: 1,
        log: 1,
        permission: 1,
        show: 1,
        sort: 0
      };
      this.modalVisible = true;
    },
    changeSelect: function changeSelect(v) {
      this.selectList = v;
    },
    submitAdd: function submitAdd() {
      var vm = this;
      vm.$refs.formAdd.validate(function (valid) {
        if (valid) {
          vm.submitLoading = true;
          add(vm.formAdd).then(function (response) {
            vm.$Message.success(response.data.msg);
            vm.getList();
            vm.submitLoading = false;
            vm.modalVisible = false;
          }).catch(function () {
            vm.submitLoading = false;
          });
        }
      });
    },
    submitEdit: function submitEdit() {
      var vm = this;
      vm.$refs.form.validate(function (valid) {
        if (valid) {
          if (!vm.form.id) {
            vm.$Message.warning('请先点击选择要修改的节点');
            return;
          }

          vm.submitLoading = true;
          edit(vm.form).then(function (response) {
            vm.$Message.success(response.data.msg);
            vm.getList();
            vm.submitLoading = false;
            vm.modalVisible = false;
          }).catch(function () {
            vm.submitLoading = false;
          });
        }
      });
    },
    delAll: function delAll() {
      var _this = this;

      this.$Modal.confirm({
        title: '确认删除',
        content: '您确认要删除所选的 ' + this.selectList.length + ' 条数据及其下级所有数据?',
        loading: true,
        onOk: function onOk() {
          var vm = _this;

          var ids = _this.selectList.map(function (v) {
            return v.id;
          }).toString();

          del(ids).then(function (response) {
            vm.getList();
            vm.$Message.success(response.data.msg);
            vm.$Modal.remove();
            vm.cancelEdit();
          });
        }
      });
    },
    search: function search() {
      var vm = this;

      if (vm.searchKey) {
        vm.loading = true;

        _getList(vm.searchKey).then(function (response) {
          vm.data = response.data.data.list;
          vm.loading = false;
        });
      } else {
        vm.getList(1);
      }
    }
  },
  mounted: function mounted() {
    // 计算高度
    var height = document.documentElement.clientHeight;
    this.maxHeight = Number(height - 310) + 'px';
    this.getList();
  }
};
