import { icons } from '@/libs/icons'
export default {
  name: 'IconChoose',
  props: {
    value: {
      type: String,
      default: ''
    },
    size: String,
    placeholder: {
      type: String,
      default: '输入图标名或选择图标'
    },
    disabled: {
      type: Boolean,
      default: false
    },
    readonly: {
      type: Boolean,
      default: false
    },
    maxlength: Number,
    icon: {
      type: String,
      default: 'md-ionic'
    }
  },
  data () {
    return {
      iconModalVisible: false,
      currentValue: this.value,
      iconData: [],
      key: '',
      tip: '输入英文关键词搜索，比如 success'
    }
  },
  methods: {
    init () {
      let re = []
      icons.forEach(e => {
        e.icons.forEach(item => {
          re.push(item)
        })
      })
      this.iconData = re
    },
    handleInput () {
      if (this.key) {
        // 搜索
        let re = []
        icons.forEach(e => {
          e.tags.forEach(item => {
            if (item.indexOf(this.key) >= 0) {
              e.icons.forEach(r => {
                re.push(r)
              })
            }
          })
        })
        this.iconData = re
      } else {
        this.init()
      }
    },
    handleFocus () {
      if (!this.key) {
        this.tip = ''
      }
    },
    handleBlur () {
      if (!this.key) {
        this.tip = '输入英文关键词搜索，比如 success'
      }
    },
    handleChange (v) {
      this.$emit('input', this.currentValue)
      this.$emit('on-change', this.currentValue)
    },
    setCurrentValue (value) {
      if (value === this.currentValue) {
        return
      }
      this.currentValue = value
    },
    hanleChoose (v) {
      this.currentValue = v
      this.$emit('input', this.currentValue)
      this.$emit('on-change', this.currentValue)
      this.iconModalVisible = false
    }
  },
  watch: {
    value (val) {
      this.setCurrentValue(val)
    }
  },
  created () {
    this.init()
  }
}
